// NavigationBar.jsx
import React, { useState } from 'react';

const menuItems = [
  { id: 'genres', label: 'Genres' },
  { id: 'artists', label: 'Artists' },
  { id: 'albums', label: 'Albums' },
  { id: 'mixes', label: 'Mixes' },
  { id: 'labels', label: 'Labels' },
];

function NavigationBar() {
  const [selected, setSelected] = useState('genres');

  return (
    <nav style={{ display: 'flex', justifyContent: 'space-around', background: '#333' }}>
      {menuItems.map((item) => (
        <div
          key={item.id}
          style={{
            padding: '10px 20px',
            cursor: 'pointer',
            backgroundColor: selected === item.id ? '#007bff' : 'transparent',
            color: selected === item.id ? 'white' : '#ccc',
          }}
          onClick={() => setSelected(item.id)}
        >
          {item.label}
        </div>
      ))}
    </nav>
  );
}

export default NavigationBar;
